document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("#theme-toggle"),t=document.body,n=localStorage.getItem("theme");"dark"===n&&t.classList.add("dark-theme"),e&&e.addEventListener("click",()=>{t.classList.toggle("dark-theme"),localStorage.setItem("theme",t.classList.contains("dark-theme")?"dark":"light")})});
